import React, { useState } from "react";
import { Link } from "react-router-dom";
import Proptype from "prop-types";
import "./Movie.css";

function Movie({ id, year, title, summary, poster, genres, rating, runtime, backgroundImage}) {
  [onMouseOver, setOnMouseOver] = useState(false);
  
  function handleFocus(e){
    setOnMouseOver(true);
  }

  

  return (
    <Link to={{
      pathname: `/movie/${id}`,
      state: {
        year,
        title,
        summary,
        poster,
        genres
      }
    }}>
      <div className="movie" onFocus={handleFocus}>
        <img src={poster} alt={title} onMouseOver ={handleFocus}/>
        {onMouseOver ? <div>asd</div>: null}
      </div>
    </Link>
  );
}

Movie.prototype = {
  key: Proptype.number.isRequired,
  id: Proptype.number.isRequired,
  year: Proptype.number.isRequired,
  tile: Proptype.string.isRequired,
  summary: Proptype.string.isRequired,
  poster: Proptype.string.isRequired,
  genre: Proptype.arrayOf(Proptype.string).isRequired
}

export default Movie;
