import defaultAxios from "axios";
import { useState } from "react";

const useAxios = (opts, axiosInstance = defaultAxios) => {
  const [state, setState] = useState({
    loading: true,
    error: null,
    data: null
  })

  if (!opts.url) {
    return;
  }

  return state;
};

export default useAxios;
