import React, { useEffect, useState, useRef} from "react";
import Movie from "./Movie";
import "./MovieList.css";
import { faAngleRight } from '@fortawesome/free-solid-svg-icons'
import { faAngleLeft } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";





function MovieList({movies}) {
  //console.log(movies);

  //const인데 어떻게 각 container에 붙을 수가 있는거지?
  const containerRef = useRef(null);
  
  function handleScrollRight(){
    containerRef.current.scrollBy(600, 0);
  }

  function handleScrollLeft(){
    containerRef.current.scrollBy(-600, 0);
  }

  return (
    <section className="container" ref = {containerRef}>
      <FontAwesomeIcon icon={faAngleRight} className="rightBtn" onClick={handleScrollRight}/>
      <FontAwesomeIcon icon={faAngleLeft} className="leftBtn" onClick={handleScrollLeft}/>
      { 
        movies &&
        movies.map(movie => {
          console.log("not loading")
          return (
            <>
              <div key={movie.id} className="movies">
                {
                  <Movie
                    key = {movie.id}
                    year={movie.year}
                    title={movie.title}
                    summary={movie.summary}
                    poster={movie.medium_cover_image}
                    genres={movie.genres}
                    rating={movie.rating}
                    runtime={movie.runtime}
                    backgroundImage={movie.background_image}
                  />
                }
              </div>
            </>
          );
        })
      }



    </section >
  );
}

function clickBtn(e){
  console.log(e);
  
}


export default MovieList;
